    function oCorner () {
    
        /* PUBLIC */
        this._minusX = 0;
        this._minusY = 0;
    
        this.x = 0;
        this.y = 0;
    }
    
    oCorner.prototype.setPos = function (new_x, new_y, direction){
        direction = direction || 1;
    
        if (keepAspect == 'yes') {
            this._minusX = new_x - this.x;
            this.x += this._minusX;
            this.y += direction * this._minusX;
        } else {
            this.x = new_x;
            this.y = new_y;
        }
    }    
    
    oCorner.prototype.checkTopLeftCorner = function(right, bottom){
        if(this.x < 0){
            this.x = 0;
        }
        if(this.y < 0){
            this.y = 0;
        }
        if(this.x > right - 10){
            this.x = right - 10;
        }
        if(this.y >= bottom -10){
            this.y = bottom -10;
        }
    }
    
    oCorner.prototype.checkTopRightCorner = function(left, bottom, img_x){
        if(this.x > img_x){
            this.x = img_x;
        }
        if(this.y < 0){
            this.y = 0;
        }
        if(this.x < left + 10){
            this.x = left + 10;
        }
        if(this.y >= bottom -10){
            this.y = bottom -10;
        }
    }
    
    oCorner.prototype.checkBottomLeftCorner = function(top, right, img_y){
        if(this.x < 0){
            this.x = 0;
        }
        if(this.y > img_y){
            this.y = img_y;
        }
        if(this.x > right - 10){
            this.x = right - 10;
        }
        if(this.y <= top + 10){
            this.y = top + 10;
        }
    }
    
    oCorner.prototype.checkBottomRightCorner = function(top, left, img_x, img_y){
        if(this.x > img_x){
            this.x = img_x;
        }
        if(this.y > img_y){
            this.y = img_y;
        }
        if(this.x < left + 10){
            this.x = left + 10;
        }
        if(this.y <= top + 10){
            this.y = top + 10;
        }
    }







